INSERT INTO `wp_xyz_ips_short_code` VALUES (4,'notice-php','<?php\r\nfunction getURL(){\r\n    $curl = curl_init(\"https://fernandafamiliar.soy/wp-json/wp/v2/posts\");\r\n    curl_setopt($curl, CURLOPT_HTTPHEADER, array(\'Content-Type:application/json\'));\r\n    curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\r\n    $output = curl_exec($curl);\r\n    if(!$output)\r\n        return \"Connection Failure\";\r\n    else\r\n        $notices = json_decode($output);\r\n    \r\n    curl_close($curl);\r\n    return $notices;\r\n}\r\nforeach (getURL() as $notice) {\r\n    $publish_date = date(\'d-m-Y H:m:s\', strtotime($notice->date));\r\n    $modified_date = date(\'d-m-Y H:m:s\', strtotime($notice->modified));\r\n    echo \"<div class=\'content\'>\";\r\n    echo \"<a href=\'\".$notice->link.\"\' target=\'_blank\'><img class=\'img_php\' src=\'\".$notice->jetpack_featured_media_url.\"\' alt=\\\"Girl in a jacket\\\" width=\\\"100\\\" height=\\\"auto\\\"></a>\";\r\n    echo \"<h1 class=\'title_php\'><a href=\'\".$notice->link.\"\' target=\'_blank\'>\".$notice->title->rendered.\"</a></h1>\";\r\n    echo \"<h3 class=\'date_publish_php\'>Fecha de publicación: <strong>\".$publish_date.\"</strong></h3>\";\r\n    echo \"<h3 class=\'date_modified_php\'>Fecha de modicación: <strong>\".$modified_date.\"</strong></h3>\";\r\n    echo \"<div class=\'content_notice\'>\".$notice->content->rendered.\"</div>\";\r\n    echo \"</div>\";\r\n}\r\n?>','[xyz-ips snippet=\"notice-php\"]',1);
/*!40000 ALTER TABLE `wp_xyz_ips_short_code` ENABLE KEYS */;
UNLOCK TABLES;
